const connection = new EvmPriceServiceConnection("https://hermes.pyth.network"); // See Hermes endpoints section below for other endpoints

const priceIds = [
  // You can find the ids of prices at https://pyth.network/developers/price-feed-ids#pyth-evm-stable
  "0xe62df6c8b4a85fe1a67db44dc12de5db330f7ac66b72dc658afedf0f4a415b43", // BTC/USD price id
  "0xff61491a931112ddf1bd8147cd1b641375f79f5825126d665480874634fd0ace", // ETH/USD price id
];

(async () => {
  const priceInfos = await connection.getPriceFeeds(priceIds);
  for (const priceInfo of priceInfos) {
    console.log(`Price ID: ${priceInfo.priceId}`);
    console.log(`Price: ${priceInfo.price}`);
    console.log(`Timestamp: ${priceInfo.timestamp}`);
    console.log(`Consensus: ${priceInfo.consensus}`);
    console.log(`-------------------`);
  }
})();
